static void vga_write_regs(const uint8_t *regs)
{
    outb(0x3C2, *regs++);

    for (uint8_t i = 0; i < 5; ++i)
    {
        outb(0x3C4, i);
        outb(0x3C5, regs[i]);
    }
    regs += 5;

    outb(0x3D4, 0x11);
    uint8_t unlock = inb(0x3D5);
    outb(0x3D4, 0x11);
    outb(0x3D5, unlock & ~0x80);

    for (uint8_t i = 0; i < 25; ++i)
    {
        outb(0x3D4, i);
        outb(0x3D5, regs[i]);
    }
    regs += 25;

    for (uint8_t i = 0; i < 9; ++i)
    {
        outb(0x3CE, i);
        outb(0x3CF, regs[i]);
    }
    regs += 9;

    for (uint8_t i = 0; i < 21; ++i)
    {
        (void)inb(0x3DA);
        outb(0x3C0, i);
        outb(0x3C0, regs[i]);
    }

    (void)inb(0x3DA);
    outb(0x3C0, 0x20);
}

static void vga_capture_state(void)
{
    if (vga_state_saved)
