void video_draw_rect_outline(int x, int y, int width, int height, uint16_t color)
{
    if (width <= 0 || height <= 0)
    {
        return;
    }

    video_draw_rect(x, y, width, 1, color);
    video_draw_rect(x, y + height - 1, width, 1, color);
    video_draw_rect(x, y, 1, height, color);
    video_draw_rect(x + width - 1, y, 1, height, color);
}

static void video_draw_char(int x, int y, char c, uint16_t fg, uint16_t bg)
{
    unsigned char ch = (unsigned char)c;
    size_t glyph_offset = (size_t)ch * 32;
    if (glyph_offset + FONT_HEIGHT > VGA_FONT_BYTES)
    {
        return;
    }

    const uint8_t *glyph = &saved_font[glyph_offset];
    for (int row = 0; row < FONT_HEIGHT; ++row)
    {
        int dst_y = y + row;
        if (dst_y < 0 || dst_y >= VIDEO_HEIGHT)
        {
            continue;
        }
        uint8_t bits = glyph[row];
        int dst_x = x;
        for (int col = 0; col < FONT_WIDTH; ++col, ++dst_x)
        {
            if (dst_x < 0 || dst_x >= VIDEO_WIDTH)
            {
                continue;
            }
            uint16_t color = (bits & (0x80 >> col)) ? fg : bg;
            backbuffer[dst_y * VIDEO_WIDTH + dst_x] = color;
        }
    }
}

void video_draw_text(int x, int y, const char *text, uint16_t fg, uint16_t bg)
{
    if (!text)
    {
        return;
    }
    for (size_t i = 0; text[i] != '\0'; ++i)
    {
        video_draw_char(x + (int)(i * FONT_WIDTH), y, text[i], fg, bg);
    }
}

bool video_enter_mode(void)
{
    if (!video_hw_enable())
    {
        video_log("video_hw_enable failed");
        return false;
    }
    video_mouse_log_count = 0;
    mouse_reset_debug_counter();
    video_log("video mode enabled, preparing scene");

    color_background = make_color(0xFF, 0x80, 0x20);
    color_window_border = make_color(0x20, 0x20, 0x20);
    color_window_title = make_color(0x30, 0x60, 0xA0);
    color_window_title_text = make_color(0xFF, 0xFF, 0xFF);
    color_window_body = make_color(0xF0, 0xF0, 0xF0);
    color_button_face = make_color(0xE0, 0xE0, 0xE0);
    color_button_border = make_color(0x40, 0x40, 0x40);
    color_button_text = make_color(0x10, 0x10, 0x10);
    color_desktop_icon_face = make_color(0x50, 0x90, 0xD0);
    color_desktop_icon_text = color_window_title_text;

