+    outw(g_io_base + RTL_REG_IMR, RTL_ISR_ROK | RTL_ISR_RER | RTL_ISR_TOK | RTL_ISR_TER);
+    outb(g_io_base + RTL_REG_CR, (uint8_t)(RTL_CR_RE | RTL_CR_TE));
+
+    for (int i = 0; i < 6; ++i)
+    {
+        g_mac[i] = inb(g_io_base + RTL_REG_IDR0 + i);
+    }
