+}
+
+void rtl8139_on_irq(void)
+{
+    if (!g_rtl_present)
+    {
+        return;
+    }
+
+    uint16_t status = inw(g_io_base + RTL_REG_ISR);
+    if (status == 0)
+    {
+        return;
+    }
+
+    outw(g_io_base + RTL_REG_ISR, status);
+
+    if (status & RTL_ISR_RER)
+    {
+        rtl8139_log("rx error");
+    }
+    if (status & RTL_ISR_TER)
