    14	static void log_pci_devices(void)
    15	{
    16	    log_dual_line("  PCI devices:");
    17	    bool any = false;
    18	
    19	    /* Limit enumeration to bus 0 for stability during bring-up */
    20	    for (uint16_t bus = 0; bus < 1; ++bus)
    21	    {
    22	        for (uint8_t device = 0; device < 32; ++device)
    23	        {
    24	            pci_device_t dev = { .bus = (uint8_t)bus, .device = device, .function = 0 };
    25	            uint16_t vendor = pci_config_read16(dev, 0x00);
    26	            if (vendor == 0xFFFF)
    27	            {
    28	                continue;
    29	            }
    30	            uint8_t header = pci_config_read8(dev, 0x0E);
    31	            uint8_t last_function = (header & 0x80U) ? 7 : 0;
    32	            for (uint8_t function = 0; function <= last_function; ++function)
    33	            {
    34	                dev.function = function;
    35	                vendor = pci_config_read16(dev, 0x00);
    36	                if (vendor == 0xFFFF)
    37	                {
    38	                    continue;
    39	                }
    40	                uint16_t device_id = pci_config_read16(dev, 0x02);
    41	                uint8_t class_code = pci_config_read8(dev, 0x0B);
    42	                uint8_t subclass = pci_config_read8(dev, 0x0A);
    43	                uint8_t prog_if = pci_config_read8(dev, 0x09);
    44	
    45	                char line[160];
    46	                size_t idx = 0;
    47	                append_text(line, &idx, sizeof(line), "    ");
    48	                append_hex(line, &idx, sizeof(line), bus, 2);
    49	                append_char(line, &idx, sizeof(line), ':');
    50	                append_hex(line, &idx, sizeof(line), device, 2);
    51	                append_char(line, &idx, sizeof(line), '.');
    52	                append_hex(line, &idx, sizeof(line), function, 1);
    53	                append_text(line, &idx, sizeof(line), " vendor 0x");
    54	                append_hex(line, &idx, sizeof(line), vendor, 4);
    55	                append_text(line, &idx, sizeof(line), " device 0x");
    56	                append_hex(line, &idx, sizeof(line), device_id, 4);
    57	                append_text(line, &idx, sizeof(line), " class ");
    58	                append_hex(line, &idx, sizeof(line), class_code, 2);
    59	                append_char(line, &idx, sizeof(line), '/');
    60	                append_hex(line, &idx, sizeof(line), subclass, 2);
    61	                append_char(line, &idx, sizeof(line), '/');
    62	                append_hex(line, &idx, sizeof(line), prog_if, 2);
    63	                log_dual_line(line);
    64	                any = true;
    65	            }
    66	        }
    67	    }
    68	
    69	    if (!any)
    70	    {
    71	        log_dual_line("    (no PCI devices detected)");
    72	    }
    73	}
