        buffer[*index] = tmp[--pos];
        (*index)++;
    }
    buffer[*index] = '\0';
}

static void append_hex(char *buffer, size_t *index, size_t capacity, uint64_t value, uint8_t digits)
{
    if (!buffer || !index || capacity == 0)
    {
        return;
    }
