static void shell_print_prompt(void)
{
    /* Avoid string literals during early bring-up. */
    console_putc('>');
    console_putc(' ');
    serial_emit_char('>');
    serial_emit_char(' ');
}

void kernel_main(void)
{
    serial_init();
    keyboard_init();
    console_init();
    console_clear();
    serial_write_char('k');
    hwinfo_print_boot_summary();
    serial_write_char('h');
    /* Defer video_init until start_video to avoid touching VGA state here */
    serial_write_char('v');
    vfs_init();
    serial_write_char('f');
    interrupts_init();
    serial_write_char('I');
    timer_init(100);
    serial_write_char('T');

    /* Network init deferred until after CLI is up */
    rtl8139_init();
    serial_write_char('N');
    /* Defer mouse listener + streaming until graphics mode */
    serial_write_char('m');
    serial_write_char('E'); /* before enabling */
    interrupts_enable();
    serial_write_char('e'); /* after enabling */


    /* Emit a simple banner. Also exercise .rodata by printing a literal. */
    serial_write_char('S');
    const char *rt = " RODATA TEST ";
    serial_write_char(' ');
    serial_write_char('[');
    serial_write_hex64((uint64_t)(uintptr_t)rt);
    serial_write_char(']');
    serial_write_char(' ');
    for (const char *p = rt; *p; ++p) { serial_emit_char(*p); }
    serial_emit_char('\n');
    const char msg[] = { 'S','h','e','l','l',' ','r','e','a','d','y','\0' };
    for (size_t i = 0; msg[i]; ++i) { console_putc(msg[i]); serial_emit_char(msg[i]); }
    console_putc('\n'); serial_emit_char('\n');

    shell_state_t shell = { .cwd = vfs_root() };
    char input[INPUT_CAPACITY];

    serial_write_char('L');
    while (1)
    {
        shell_print_prompt();
        size_t len = cli_read_line(input, INPUT_CAPACITY);
        (void)len;
        shell_process_line(&shell, input);
        rtl8139_poll();
    }
}
