   319	static void log_pci_devices(void)
   320	{
   321	    log_dual_line("  PCI devices:");
   322	    bool any = false;
   323	
   324	    /* Limit enumeration to bus 0 for stability during bring-up */
   325	    for (uint16_t bus = 0; bus < 1; ++bus)
   326	    {
   327	        for (uint8_t device = 0; device < 32; ++device)
   328	        {
   329	            pci_device_t dev = { .bus = (uint8_t)bus, .device = device, .function = 0 };
   330	            uint16_t vendor = pci_config_read16(dev, 0x00);
   331	            if (vendor == 0xFFFF)
   332	            {
   333	                continue;
   334	            }
   335	            uint8_t header = pci_config_read8(dev, 0x0E);
   336	            uint8_t last_function = (header & 0x80U) ? 7 : 0;
   337	            for (uint8_t function = 0; function <= last_function; ++function)
   338	            {
   339	                dev.function = function;
   340	                vendor = pci_config_read16(dev, 0x00);
   341	                if (vendor == 0xFFFF)
   342	                {
   343	                    continue;
   344	                }
   345	                uint16_t device_id = pci_config_read16(dev, 0x02);
   346	                uint8_t class_code = pci_config_read8(dev, 0x0B);
   347	                uint8_t subclass = pci_config_read8(dev, 0x0A);
   348	                uint8_t prog_if = pci_config_read8(dev, 0x09);
   349	
   350	                char line[160];
   351	                size_t idx = 0;
   352	                append_text(line, &idx, sizeof(line), "    ");
   353	                append_hex(line, &idx, sizeof(line), bus, 2);
   354	                append_char(line, &idx, sizeof(line), ':');
   355	                append_hex(line, &idx, sizeof(line), device, 2);
   356	                append_char(line, &idx, sizeof(line), '.');
   357	                append_hex(line, &idx, sizeof(line), function, 1);
   358	                append_text(line, &idx, sizeof(line), " vendor 0x");
   359	                append_hex(line, &idx, sizeof(line), vendor, 4);
   360	                append_text(line, &idx, sizeof(line), " device 0x");
   361	                append_hex(line, &idx, sizeof(line), device_id, 4);
   362	                append_text(line, &idx, sizeof(line), " class ");
   363	                append_hex(line, &idx, sizeof(line), class_code, 2);
   364	                append_char(line, &idx, sizeof(line), '/');
   365	                append_hex(line, &idx, sizeof(line), subclass, 2);
   366	                append_char(line, &idx, sizeof(line), '/');
   367	                append_hex(line, &idx, sizeof(line), prog_if, 2);
   368	                log_dual_line(line);
   369	                any = true;
   370	            }
   371	        }
   372	    }
   373	
   374	    if (!any)
   375	    {
   376	        log_dual_line("    (no PCI devices detected)");
   377	    }
   378	}
