static vfs_node_t *vfs_find_child(vfs_node_t *parent, const char *name)
{
    if (!parent)
    {
        return NULL;
    }
    vfs_node_t *node = parent->first_child;
    while (node)
    {
        if (strcmp(node->name, name) == 0)
        {
            return node;
        }
        node = node->next_sibling;
