    size_t len = 0;
    while (*path && *path != '/')
    {
        if (len < VFS_NAME_MAX - 1)
        {
            out[len++] = *path;
        }
        ++path;
    }
    out[len] = '\0';
    *path_ptr = path;
    return true;
}

