}

bool vfs_append(vfs_node_t *file, const char *data, size_t len)
{
    if (!file || file->is_dir)
    {
        return false;
    }
    if (file->size + len > file->capacity)
    {
        return false;
    }
    memmove(file->data + file->size, data, len);
    file->size += len;
    ensure_terminator(file);
    return true;
}

