static void video_log_hex(const char *prefix, uint64_t value)
{
    static const char hex[] = "0123456789ABCDEF";
    char buf[17];
    buf[16] = '\0';
    for (int i = 15; i >= 0; --i)
    {
        buf[i] = hex[value & 0xF];
        value >>= 4;
