{"output":"#include "shell_commands.h"

#include "rtl8139.h"
#include "libc.h"

static bool shell_cmd_ip_link_show(shell_output_t *out);

bool shell_cmd_ip(shell_state_t *shell, shell_output_t *out, const char *args)
{
    (void)shell;
    if (!args || *args == '\0')
    {
        return shell_cmd_ip_link_show(out);
    }

    const char *cursor = args;
    while (*cursor && (*cursor == ' ' || *cursor == '\t'))
    {
        ++cursor;
    }

    if (strncmp(cursor, "link", 4) == 0 && (cursor[4] == '\0' || cursor[4] == ' ' || cursor[4] == '\t'))
    {
        cursor += 4;
        while (*cursor && (*cursor == ' ' || *cursor == '\t'))
        {
            ++cursor;
        }
        if (*cursor == '\0' || strncmp(cursor, "show", 4) == 0)
        {
            return shell_cmd_ip_link_show(out);
        }
    }

    shell_print_error("Usage: ip link [show]");
    return false;
}

static bool shell_cmd_ip_link_show(shell_output_t *out)
{
    if (!rtl8139_is_present())
    {
        return shell_output_write(out, "No network interfaces detected\n");
    }

    uint8_t mac[6];
    if (!rtl8139_get_mac(mac))
    {
        return false;
    }

    if (!shell_output_write(out, "1: rtl0: <UP> mtu 1500 qdisc noop state UNKNOWN\n    link/ether "))
    {
        return false;
    }

    static const char hex[] = "0123456789ABCDEF";
    for (int i = 0; i < 6; ++i)
    {
        char buf[3];
        buf[0] = hex[(mac[i] >> 4) & 0xF];
        buf[1] = hex[mac[i] & 0xF];
        buf[2] = '\0';
        if (!shell_output_write(out, buf))
        {
            return false;
        }
        if (i != 5 && !shell_output_write(out, ":"))
        {
            return false;
        }
    }

    return shell_output_write(out, " brd ff:ff:ff:ff:ff:ff\n");
}
