+    eth[12] = 0x08;
+    eth[13] = 0x00;
+
+    memset(ip, 0, 20);
+    ip[0] = 0x45;
+    ip[1] = 0x00;
+    ip[8] = 64;
+    ip[9] = 17;
+    write_be32(ip + 12, 0);
