+    write_be16(ip + 10, ip_sum);
+
+    write_be16(udp + 6, 0);
+    uint16_t udp_sum = udp_checksum(ip + 12, ip + 16, udp, udp_len);
+    if (udp_sum == 0)
+    {
+        udp_sum = 0xFFFF;
+    }
+    write_be16(udp + 6, udp_sum);
+
+    size_t frame_len = 14 + ip_len;
+    if (frame_len < 60)
+    {
+        memset(buffer + frame_len, 0, 60 - frame_len);
+        frame_len = 60;
+    }
+
+    return frame_len;
+}
