        udp_csum = 0xFFFF;
    }
    write_be16(udp + 6, udp_csum);

    size_t frame_len = 14 + ip_len;
    if (frame_len < 60)
    {
        memset(buffer + frame_len, 0, 60 - frame_len);
        frame_len = 60;
    }

    serial_write_string("dhcp: transmitting message type ");
    serial_write_char((char)('0' + msg_type));
    serial_write_string("\r\n");

    if (!net_if_send(g_active_iface, buffer, frame_len))
    {
        serial_write_string("dhcp: failed to transmit frame\r\n");
        return false;
    }
    return true;
}

