static uint32_t pci_build_address(pci_device_t dev, uint8_t offset)
{
    return 0x80000000U
        | ((uint32_t)dev.bus << 16)
        | ((uint32_t)dev.device << 11)
        | ((uint32_t)dev.function << 8)
