    console_write("In-memory FS shell ready. Commands: echo, cat, mkdir, ls, ip, dhclient, start_video.\n");
    serial_write_string("In-memory FS shell ready. Commands: echo, cat, mkdir, ls, ip, dhclient, start_video.\r\n");

    while (1)
    {
        shell_print_prompt();
        size_t len = cli_read_line(input, INPUT_CAPACITY);
        (void)len;
        shell_process_line(&shell, input);
