bool shell_cmd_ping(shell_state_t *shell, shell_output_t *out, const char *args)
{
    (void)shell;
    const char *cursor = args ? args : "";

    char iface_name[NET_IF_NAME_MAX];
    char ip_token[32];

    if (!read_token(&cursor, iface_name, sizeof(iface_name)) ||
