    char source_str[32];
    net_format_ipv4(target_ip, target_str);
    net_interface_t *iface = requested_iface;
    uint32_t next_hop_ip = target_ip;
    if (!net_route_next_hop(iface, target_ip, &iface, &next_hop_ip))
    {
        shell_print_error("no route to host");
        return false;
    }
