{"output":"#include "atk_button.h"

#include "libc.h"
#include "video.h"

void atk_button_set_title(atk_button_t *btn, const char *title)
{
    if (!btn || !title)
    {
        return;
    }

    size_t i = 0;
    for (; title[i] != '\0' && i < ATK_BUTTON_TITLE_MAX - 1; ++i)
    {
        btn->title[i] = title[i];
    }
    btn->title[i] = '\0';
}

int atk_button_effective_height(const atk_button_t *btn)
{
    if (!btn)
    {
        return 0;
    }

    int height = btn->height;
    if (btn->style == ATK_BUTTON_STYLE_TITLE_BELOW)
    {
        height += ATK_FONT_HEIGHT + 4;
    }
    return height;
}

bool atk_button_hit_test(const atk_button_t *btn, int origin_x, int origin_y, int px, int py)
{
    if (!btn || !btn->used)
    {
        return false;
    }

    int x0 = origin_x + btn->x;
    int y0 = origin_y + btn->y;
    int x1 = x0 + btn->width;
    int y1 = y0 + atk_button_effective_height(btn);

    return (px >= x0 && px < x1 && py >= y0 && py < y1);
}

void atk_button_draw(const atk_state_t *state, const atk_button_t *btn, int origin_x, int origin_y)
{
    if (!state || !btn || !btn->used)
    {
        return;
    }

    const atk_theme_t *theme = &state->theme;

    int bx = origin_x + btn->x;
    int by = origin_y + btn->y;

    uint16_t border_color = btn->absolute ? theme->button_border : theme->window_border;
    uint16_t face_color = btn->absolute ? theme->desktop_icon_face : theme->button_face;
    uint16_t text_color = btn->absolute ? theme->desktop_icon_text : theme->button_text;

    if (!btn->absolute && btn->style == ATK_BUTTON_STYLE_TITLE_INSIDE)
    {
        face_color = theme->window_title;
        text_color = theme->window_title_text;
    }

    video_draw_rect(bx, by, btn->width, btn->height, face_color);
    video_draw_rect_outline(bx, by, btn->width, btn->height, border_color);

    int text_x = bx + 4;
    int text_y = by + 4;

    size_t title_len = strlen(btn->title);
    int title_px_width = (int)(title_len * ATK_FONT_WIDTH);

    if (btn->style == ATK_BUTTON_STYLE_TITLE_INSIDE)
    {
        if (title_px_width < btn->width)
        {
            text_x = bx + (btn->width - title_px_width) / 2;
        }
        if (btn->height > ATK_FONT_HEIGHT)
        {
            text_y = by + (btn->height - ATK_FONT_HEIGHT) / 2;
        }
        video_draw_text(text_x, text_y, btn->title, text_color, face_color);
    }
    else
    {
        int label_y = by + btn->height + 2;
        if (title_px_width < btn->width)
        {
            text_x = bx + (btn->width - title_px_width) / 2;
        }
        else
        {
            text_x = bx;
        }
        video_draw_text(text_x, label_y, btn->title, text_color, theme->background);
    }
}
