static void window_get_bounds(const atk_window_t *win, int *x, int *y, int *width, int *height)
{
    if (!win || !win->used)
    {
        if (x) *x = 0;
        if (y) *y = 0;
        if (width) *width = 0;
        if (height) *height = 0;
        return;
    }

    int bx = win->x - ATK_WINDOW_BORDER;
    int by = win->y - ATK_WINDOW_BORDER;
    int bw = win->width + ATK_WINDOW_BORDER * 2;
    int bh = win->height + ATK_WINDOW_BORDER * 2;

    if (x) *x = bx;
    if (y) *y = by;
    if (width) *width = bw;
    if (height) *height = bh;
}

static atk_button_t *atk_window_add_button(atk_window_t *win,
                                           const char *title,
                                           int rel_x,
                                           int rel_y,
                                           int width,
                                           int height,
                                           atk_button_style_t style,
                                           bool draggable,
                                           atk_button_action_t action,
                                           void *context)
{
    if (!win || win->button_count >= ATK_MAX_WINDOW_BUTTONS)
    {
        return 0;
    }
    atk_button_t *btn = &win->buttons[win->button_count++];
    memset(btn, 0, sizeof(*btn));
    btn->used = true;
    btn->absolute = false;
    btn->x = rel_x;
    btn->y = rel_y;
    btn->width = width;
    btn->height = height;
    btn->style = style;
    btn->draggable = draggable;
    btn->action = action;
    btn->action_context = context;
    atk_button_set_title(btn, title);
    return btn;
}

static void action_window_close(atk_button_t *button, void *context)
{
    (void)button;
    atk_state_t *state = atk_state_get();
    atk_window_close(state, (atk_window_t *)context);
}
