int atk_window_hit_test(const atk_state_t *state, int x, int y)
{
    if (!state)
    {
        return -1;
    }

    for (int i = state->window_count - 1; i >= 0; --i)
    {
        const atk_window_t *win = &state->windows[i];
        if (!win->widget.used)
        {
            continue;
        }
        if (x >= win->widget.x && x < win->widget.x + win->widget.width &&
            y >= win->widget.y && y < win->widget.y + win->widget.height)
        {
            return i;
        }
    }
    return -1;
}

int atk_window_title_hit_test(const atk_state_t *state, int x, int y)
{
    if (!state)
    {
        return -1;
    }

    for (int i = state->window_count - 1; i >= 0; --i)
    {
        const atk_window_t *win = &state->windows[i];
        if (!win->widget.used)
        {
            continue;
        }
        if (x >= win->widget.x && x < win->widget.x + win->widget.width &&
            y >= win->widget.y && y < win->widget.y + ATK_WINDOW_TITLE_HEIGHT)
        {
            return i;
        }
    }
    return -1;
