atk_button_t *atk_window_get_button_at(atk_window_t *win, int px, int py, int *out_index)
{
    if (!win)
    {
        return 0;
    }
    for (int i = win->button_count - 1; i >= 0; --i)
    {
        atk_button_t *btn = &win->buttons[i];
        if (!btn->widget.used)
        {
            continue;
        }
        if (atk_button_hit_test(btn, win->widget.x, win->widget.y, px, py))
        {
            if (out_index)
            {
                *out_index = i;
            }
            return btn;
        }
    }
    return 0;
}

void atk_window_mark_dirty(const atk_window_t *win)
{
    int x, y, w, h;
    window_get_bounds(win, &x, &y, &w, &h);
    if (w <= 0 || h <= 0)
    {
        return;
    }
    video_invalidate_rect(x, y, w, h);
}

void atk_window_ensure_inside(atk_window_t *win)
{
    if (!win)
    {
        return;
    }

    if (win->widget.width > VIDEO_WIDTH)
    {
        win->widget.width = VIDEO_WIDTH;
    }
    if (win->widget.height > VIDEO_HEIGHT)
    {
        win->widget.height = VIDEO_HEIGHT;
    }

    int max_x = VIDEO_WIDTH - win->widget.width;
    int max_y = VIDEO_HEIGHT - win->widget.height;

    if (win->widget.x < 0) win->widget.x = 0;
    if (win->widget.y < 0) win->widget.y = 0;
    if (win->widget.x > max_x) win->widget.x = max_x;
    if (win->widget.y > max_y) win->widget.y = max_y;
}

