        state->dragging_desktop_button < state->desktop_button_count)
    {
        atk_button_t *btn = &state->desktop_buttons[state->dragging_desktop_button];
        atk_button_t old_btn = *btn;

        int new_x = cursor_x - state->desktop_drag_offset_x;
        int new_y = cursor_y - state->desktop_drag_offset_y;

        if (new_x < 0) new_x = 0;
        if (new_y < 0) new_y = 0;
        int max_x = VIDEO_WIDTH - btn->widget.width;
        int max_y = VIDEO_HEIGHT - atk_button_effective_height(btn);
        if (max_x < 0) max_x = 0;
        if (max_y < 0) max_y = 0;
        if (new_x > max_x) new_x = max_x;
        if (new_y > max_y) new_y = max_y;

        btn->widget.x = new_x;
        btn->widget.y = new_y;

