void net_format_ipv4(uint32_t addr, char *out)
{
    char *ptr = out;
    for (int i = 3; i >= 0; --i)
    {
        uint8_t byte = (uint8_t)((addr >> (i * 8)) & 0xFF);
        if (byte >= 100)
        {
            *ptr++ = (char)('0' + (byte / 100));
            byte %= 100;
        }
        if (byte >= 10 || (ptr != out && *(ptr - 1) != '.'))
        {
            *ptr++ = (char)('0' + (byte / 10));
            byte %= 10;
        }
        *ptr++ = (char)('0' + byte);
        if (i != 0)
        {
            *ptr++ = '.';
        }
    }
    *ptr = '\0';
}

