    }

    net_interface_t *iface = net_if_by_name(g_default_route.iface_name);
    if (!iface || !iface->present)
    {
        g_default_route.valid = false;
        g_default_route.gateway = 0;
        g_default_route.iface_name[0] = '\0';
        return false;
    }

    if (iface_out)
