        }
        if (status & 0x40000000U)
        {
            serial_write_string("rtl8139: tx abort reported\r\n");
            return false;
        }
        if (status & 0x00004000U)
        {
            serial_write_string("rtl8139: tx underrun reported\r\n");
            return false;
        }
    }
    return false;
}

static void rtl8139_tx_recover(int slot)
{
    uint32_t tsd_reg = RTL_REG_TSD0 + slot * 4;
    uint32_t status = inl(g_io_base + tsd_reg);
    outl(g_io_base + tsd_reg, status | 0x40000000U); /* request abort */

