        }
    }

    uint8_t cr = inb(g_io_base + RTL_REG_CR);
    outb(g_io_base + RTL_REG_CR, (uint8_t)(cr & ~RTL_CR_TE));
    outb(g_io_base + RTL_REG_CR, (uint8_t)(cr | RTL_CR_TE));
}
