                        atk_window_mark_dirty(before);
                        atk_window_mark_dirty(front_win);
                        result.redraw = true;
                    }
                    state->pressed_window_button_window = front_index;
                    state->pressed_window_button_index = button_index;
                    handled = true;
                }
