                int front_index = atk_window_bring_to_front(state, title_index);
                if (front_index >= 0)
                {
                    atk_widget_t *front_win = state->windows[front_index];
                    if (front_index != title_index)
                    {
                        atk_window_mark_dirty(before);
                        atk_window_mark_dirty(front_win);
                        result.redraw = true;
