                    atk_widget_t *btn = atk_window_button_at_index(win, state->pressed_window_button_index);
                    if (btn && btn->used && atk_button_hit_test(btn, win->x, win->y, cursor_x, cursor_y))
                    {
                        atk_button_invoke(btn);
                        result.redraw = true;
                    }
