+
+const atk_class_t ATK_TEXT_INPUT_CLASS = { "TextInput", &ATK_WIDGET_CLASS, &text_input_vtable, sizeof(atk_text_input_priv_t) };
+
+void atk_text_input_configure(atk_widget_t *widget, const char *initial_text)
+{
+    if (!widget)
+    {
+        return;
+    }
+
+    atk_text_input_priv_t *priv = text_input_priv_mut(widget);
+    if (!priv)
+    {
+        return;
+    }
+
+    widget->used = true;
+    atk_text_input_set_text(widget, initial_text ? initial_text : "");
+}
+
+void atk_text_input_set_text(atk_widget_t *widget, const char *text)
+{
+    atk_text_input_priv_t *priv = text_input_priv_mut(widget);
+    if (!priv)
+    {
+        return;
+    }
+
+    const char *src = text ? text : "";
+    size_t i = 0;
+    for (; src[i] != '\0' && i < ATK_TEXT_INPUT_MAX_CHARS - 1; ++i)
+    {
+        priv->text[i] = src[i];
+    }
+    priv->text[i] = '\0';
+    priv->length = i;
+}
+
+const char *atk_text_input_text(const atk_widget_t *widget)
+{
+    const atk_text_input_priv_t *priv = text_input_priv(widget);
+    if (!priv)
+    {
+        return "";
+    }
+    return priv->text;
+}
+
+void atk_text_input_draw(const atk_state_t *state, const atk_widget_t *widget, int origin_x, int origin_y)
+{
+    if (!state || !widget || !widget->used)
+    {
+        return;
+    }
+
+    const atk_theme_t *theme = &state->theme;
+    const atk_text_input_priv_t *priv = text_input_priv(widget);
+    (void)priv;
+
+    bool focused = (state->focused_text_input == widget);
+
+    int x0 = origin_x + widget->x;
+    int y0 = origin_y + widget->y;
+
+    video_draw_rect(x0, y0, widget->width, widget->height, theme->text_input_background);
+    video_draw_rect_outline(x0, y0, widget->width, widget->height, focused ? theme->text_input_focused_border : theme->text_input_border);
+
+    const char *text = atk_text_input_text(widget);
+    int text_x = x0 + 6;
+    int text_y = y0 + (widget->height - ATK_FONT_HEIGHT) / 2;
+    if (text_y < y0)
+    {
+        text_y = y0;
+    }
+
+    video_draw_text(text_x, text_y, text, theme->text_input_text, theme->text_input_background);
+}
+
+bool atk_text_input_hit_test(const atk_widget_t *widget, int origin_x, int origin_y, int px, int py)
+{
+    if (!widget || !widget->used)
+    {
+        return false;
+    }
+
+    int x0 = origin_x + widget->x;
+    int y0 = origin_y + widget->y;
+    int x1 = x0 + widget->width;
+    int y1 = y0 + widget->height;
+
+    return (px >= x0 && px < x1 && py >= y0 && py < y1);
+}
