+        return;
+    }
+
+    int margin = 16;
+    int desired_height = ATK_WINDOW_TITLE_HEIGHT + margin * 2 + ATK_TEXT_INPUT_HEIGHT;
+    window->height = desired_height;
+    atk_window_ensure_inside(window);
+
+    int input_width = window->width - margin * 2;
+    if (input_width < 32)
+    {
+        input_width = 32;
+    }
