   467	static void format_window_title(char *buffer, size_t capacity, int id)
   468	{
   469	    if (!buffer || capacity == 0)
   470	    {
   471	        return;
   472	    }
   473	    const char prefix[] = "Window ";
   474	    size_t pos = 0;
   475	    for (size_t i = 0; i < sizeof(prefix) - 1 && pos < capacity - 1; ++i)
   476	    {
   477	        buffer[pos++] = prefix[i];
   478	    }
   479	
   480	    char digits[16];
   481	    size_t digit_count = 0;
   482	    int value = id;
   483	    if (value <= 0)
   484	    {
   485	        digits[digit_count++] = '0';
   486	    }
   487	    else
   488	    {
   489	        while (value > 0 && digit_count < sizeof(digits))
   490	        {
   491	            digits[digit_count++] = (char)('0' + (value % 10));
