+{
+    atk_text_input_priv_t *priv = (atk_text_input_priv_t *)atk_widget_priv(input, &ATK_TEXT_INPUT_CLASS);
+    if (!priv)
+    {
+        return ATK_TEXT_INPUT_EVENT_NONE;
