   316	    if (redirect_path)
   317	    {
   318	        shell_output_init_buffer(&output);
   319	        if (!shell_output_redirect(&output, shell, redirect_path))
   320	        {
   321	            shell_output_reset(&output);
   322	            free(working);
   323	            return shell_duplicate_string("Error: redirect failed\n");
   324	        }
   325	    }
   326	    else
   327	    {
   328	        shell_output_init_buffer(&output);
   329	    }
   330	
   331	    bool handler_found = false;
   332	    bool handler_result = false;
   333	    for (size_t i = 0; i < sizeof(g_commands) / sizeof(g_commands[0]); ++i)
   334	    {
   335	        if (strcmp(line, g_commands[i].name) == 0)
   336	        {
   337	            handler_found = true;
   338	            handler_result = g_commands[i].handler(shell, &output, args);
   339	            break;
   340	        }
   341	    }
   342	
   343	    char *result = NULL;
   344	
   345	    if (!handler_found)
   346	    {
   347	        result = shell_duplicate_string("Error: unknown command\n");
   348	        handler_result = false;
   349	    }
   350	    else if (redirect_path)
   351	    {
   352	        result = shell_duplicate_empty();
   353	    }
   354	    else
   355	    {
   356	        result = shell_output_take_buffer(&output);
   357	    }
   358	
   359	    shell_output_reset(&output);
   360	    free(working);
   361	
   362	    if (!result)
   363	    {
   364	        result = shell_duplicate_empty();
   365	    }
   366	
   367	    if (success)
   368	    {
   369	        *success = handler_found && handler_result;
   370	    }
   371	    return result;
