            byte %= 10;
        }
        *ptr++ = (char)('0' + byte);
        if (i != 0)
        {
            *ptr++ = '.';
        }
    }
    *ptr = '\0';
}

bool net_parse_ipv4(const char *text, uint32_t *out_addr)
{
    if (!text || !out_addr)
    {
        return false;
    }

    uint32_t result = 0;
    uint32_t value = 0;
    int octets = 0;
    bool have_digit = false;
    const char *cursor = text;

    while (true)
    {
        char c = *cursor;
        if (c >= '0' && c <= '9')
        {
            have_digit = true;
            value = value * 10U + (uint32_t)(c - '0');
            if (value > 255U)
            {
                return false;
            }
            ++cursor;
        }
        else if (c == '.' || c == '\0')
        {
            if (!have_digit)
            {
                return false;
            }
            result = (result << 8) | value;
            ++octets;
            if (octets > 4)
            {
                return false;
            }
            if (c == '\0')
            {
                break;
            }
            value = 0;
            have_digit = false;
            ++cursor;
        }
        else
        {
            return false;
        }
    }

    if (octets != 4)
    {
        return false;
    }

    *out_addr = result;
    return true;
}
