    node->used = false;
    node->is_dir = false;
    node->name[0] = '\0';
    node->parent = NULL;
    node->first_child = NULL;
    node->next_sibling = NULL;
    node->size = 0;
    node->capacity = VFS_FILE_CAPACITY;
    node->data[0] = '\0';
}

static vfs_node_t *vfs_alloc_node(void)
{
    for (size_t i = 0; i < VFS_MAX_NODES; ++i)
    {
        if (!nodes[i].used)
        {
            nodes[i].used = true;
            nodes[i].size = 0;
            nodes[i].capacity = VFS_FILE_CAPACITY;
            nodes[i].data[0] = '\0';
            nodes[i].first_child = NULL;
            nodes[i].next_sibling = NULL;
            nodes[i].parent = NULL;
            nodes[i].name[0] = '\0';
            serial_write_char('A');
            serial_write_hex64((uint64_t)&nodes[i]);
            serial_write_char('\n');
