+        return false;
+    }
+    size_t len = 0;
+    while (start[len] && start[len] != ' ' && start[len] != '\t')
+    {
+        if (len + 1 < capacity)
+        {
+            out[len] = start[len];
+        }
+        ++len;
+    }
+    if (len >= capacity)
