+        size_t port_len = strlen(colon + 1);
+        if (port_len == 0 || port_len >= sizeof(port_part))
+        {
+            return false;
+        }
+        memcpy(port_part, colon + 1, port_len + 1);
+    }
+    else
+    {
+        size_t ip_len = strlen(text);
+        if (ip_len == 0 || ip_len >= sizeof(ip_part))
