{
    serial_write_string("rtl8139: ");
    serial_write_string(msg);
    serial_write_string("\r\n");
}

static void rtl8139_log_hex8(uint8_t value)
{
    static const char hex[] = "0123456789ABCDEF";
    char buf[3];
    buf[0] = hex[(value >> 4) & 0xF];
    buf[1] = hex[value & 0xF];
    buf[2] = '\0';
    serial_write_string(buf);
}

