    bool have_mac = net_arp_lookup(next_hop_ip, target_mac);
    uint32_t frequency = timer_frequency();
    if (frequency == 0)
    {
        frequency = 1000;
    }
    const uint64_t timeout_ticks = (uint64_t)frequency * 5;

    rtl8139_poll();

    if (!have_mac)
