        rtl8139_dump_state("irq");
    }

    if (status & (RTL_ISR_TOK | RTL_ISR_TER))
    {
        rtl8139_reclaim_tx();
        rtl8139_tx_flush_queue();
    }

    net_tcp_poll();
}

