            respond = true;
            reply_ip = target_ip;
        }

        if (respond)
        {
            serial_write_string("arp: replying for ip ");
            char ipbuf[32];
            net_format_ipv4(reply_ip, ipbuf);
            serial_write_string(ipbuf);
            serial_write_string("\r\n");
            net_arp_send_reply(iface, sender_mac, sender_ip, reply_ip);
        }
    }
}

