        uint16_t rsr   = rtl8139_buffer_read16(offset + 0);
        uint16_t rxlen = rtl8139_buffer_read16(offset + 2);   // includes CRC
        bool ok = (rsr & 0x0001U) && rxlen >= 8 && rxlen <= (RTL_RX_RING_SIZE - 16);

