static void mouse_write(uint8_t value)
{
    mouse_wait(1);
    outb(KBD_COMMAND, 0xD4);
    mouse_wait(1);
    outb(KBD_DATA, value);
    mouse_wait(0);
    (void)inb(KBD_DATA); /* ACK */
}

void mouse_register_listener(mouse_listener_t listener)
{
    g_listener = listener;
