+#define TLS_CONTENT_CHANGE_CIPHER_SPEC 20
+#define TLS_CONTENT_ALERT             21
+#define TLS_CONTENT_HANDSHAKE         22
+#define TLS_CONTENT_APPLICATION_DATA  23
