    if (!is_canonical(addr))
    {
        serial_write_char('!');
        serial_write_hex64(addr);
        serial_write_char('@');
        uint64_t ret = (uint64_t)__builtin_return_address(0);
        serial_write_hex64(ret);
        serial_write_char('\n');
        for (;;)
