   691	static void video_poll_keyboard(void)
   692	{
   693	    if (!video_active) return;
   694	
   695	    char ch = 0;
   696	    bool have_input = false;
   697	    bool redraw_needed = false;
   698	
   699	    while (keyboard_try_read(&ch))
   700	    {
   701	        have_input = true;
   702	        atk_key_event_result_t result = atk_handle_key_char(ch);
   703	        if (result.redraw) redraw_needed = true;
   704	        if (result.exit_video) exit_requested = true;
   705	    }
   706	
   707	    if (!have_input || !redraw_needed) return;
   708	
   709	    video_request_refresh();
   710	    video_perform_refresh();
   711	}
